import { getBlack } from './../theme/colors';

const DetailsPanel = props => {
    class Year {
        constructor(year) {
            this.year = new Date(year);
            this.months = [];
            this.createMonths = () => {
                for (let i = 0; i < 12; ++i ) {
                    this.months.push(this.year.getMonth(i))
                }
            }
            this.createMonths();
            console.log(this.months);
        }
    }

    const year = new Year(2020);
    console.log(year);
    return (
        <section className="details-panel">
            <h2 className="dropdown-title-selector">Months</h2>
            <section className="months-container">

            </section>
            <style jsx>{`
                .details-panel {
                    padding: 20px;
                    background: white;
                    grid-area: details;
                    border-radius: 10px;
                }
                .details-panel h2 {
                    color: ${getBlack(0.8)};
                }
            `}</style>
        </section>
    )
}

export default DetailsPanel;
