import { useState, useEffect } from 'react';

const Countdown = props => {

    const [ timeRemaining, setTimeRemaining ] = useState({
        minutes: 25,
        seconds: 0
    });

    const startTimer = () => {
        let counterInterval = setInterval(() => {
            let { seconds, minutes } = timeRemaining;
            if (seconds == 0) {
                setTimeRemaining(() => {
                    return {
                        seconds: 59,
                        minutes: --minutes
                    }
                })
            } else if (seconds == 0 && minutes == 0) {
                console.log('Time\'s up!');
            } else {
                setTimeRemaining(() => {
                    return {
                        seconds: --seconds,
                        minutes: minutes
                    }
                })
            }
        }, 1000)
    };

    useEffect(() => {
        console.log(timeRemaining);
    },[timeRemaining])

    return (
        <>
            <span 
                className="countdown" 
                onClick={startTimer}
            >
                {timeRemaining.minutes}:{timeRemaining.seconds}
            </span>
            <section className="countdown-controls">

            </section>
            <style jsx>{`
                display: inline-block;
                width: 100%;
                text-align: center;
                font-size: 6rem;
                font-weight: 500;
                .countdown-controls {
                    height: 50px;
                    width: 100%;
                    border-radius: 100px;
                    background: white;
                }
            `}</style>
        </>
    )
}

export default Countdown;
