import { useState } from 'react';

const Countdown = props => {

const [ timeRemaining, setTimeRemaining ] = useState({
    minutes: 25,
    seconds: 0
})
    const startTimer = (minutesRemaining, secondsRemaining) => {
        let counterInterval = setInterval(() => {
            timeRemaining
        }, 1000)
    }
    return (
        <>
            <span 
                className="countdown" 
                onClick={(startTimer(minutesRemaining, secondsRemaining))}
            >
                {minutesRemaining}:{secondsRemaining}
            </span>
            <section className="countdown-controls">

            </section>
            <style jsx>{`
                display: inline-block;
                width: 100%;
                text-align: center;
                font-size: 7rem;
                font-weight: 500;
                .countdown-controls {
                    height: 50px;
                    width: 100%;
                    border-radius: 100px;
                    background: white;
                }
            `}</style>
        </>
    )
}

export default Countdown;
