import { useState, useEffect } from 'react';
import Navbar from './../components/Navbar';
import TimePanel from './../components/TimePanel';
import DetailsPanel from './../components/DetailsPanel';
import users_list from './../mock_data/users_list.json';
import users_data from './../mock_data/users_data.json';

const Index = () => {

    const [currentUserData, setCurrentUserData] = useState({});

    useEffect(() => {
        setCurrentUserData(users_data[users_list[0].user_ID])
        // console.log(currentUserData);
    },[])


    return (
        <>
            <Navbar userName={currentUserData.name}/>
            <main>
                <TimePanel userData={currentUserData}/>
                <DetailsPanel userData={currentUserData}/>
                <style jsx global>{`
                    * {
                        font-family: 'Gotham Rounded', sans-serif;
                        color: white;
                        margin: 0;
                        padding: 0;
                        box-sizing: border-box;
                        font-weight: medium;
                    }
                    body {
                        background-color: #F2523A;
                        padding: 20px;
                    }
                `}
                </style>
                <style jsx>{`
                    margin-top: 20px;
                    height: 80vh;
                    width: 100%;
                    {/* background: black; */}
                    display: grid;
                    grid-template-rows: 70% 30%;
                    grid-template-columns: 50% 50%;
                    grid-template-areas: 
                        "time details"
                        "tasks details";
                    row-gap: 10px;
                    column-gap: 10px;

                    @media screen and (max-width: 720px) {
                        width: 80vw;
                    }
                `}
                </style>
            </main>
        </>
    );
}

export default Index;
