import { getBlack } from './../theme/colors';

const DetailsPanel = props => {
    const months = [
        'January',
        'February',
        'March',
        'April',
        'May',
        'June',
        'July',
        'August',
        'September',
        'October',
        'November',
        'December'
    ]
    const date = new Date();

    const turnDaysIntoArray = (month) => {
        const days = [];
        const totalMonthDays = date.getMonth(month).daysInMonth();
    }

    return (
        <section className="details-panel">
            <h2 className="dropdown-title-selector">Months</h2>
            <section className="months-container">
                {months.map((month, index) => {
                    return (
                        <section className="month" key={index}>
                            <h3 className="month-name">{month}</h3>
                            <section className="day-progress-container">
                                {/* {} */}
                            </section>
                        </section>
                    )
                })}
            </section>
            <style jsx>{`
                .details-panel {
                    padding: 20px;
                    background: white;
                    grid-area: details;
                    border-radius: 10px;
                }
                .details-panel h2 {
                    color: ${getBlack(0.8)};
                }
                .months-container {
                    display: flex;
                    justify-content: space-between;
                    flex-wrap: wrap;
                }
                .months-container .month {
                    width: 49%;
                }
                .months-container .month .month-name {
                    color: ${getBlack(.7)}
                }
            `}</style>
        </section>
    )
}

export default DetailsPanel;
