import { useState } from 'react';

const Countdown = props => {

    const [ timeRemaining, setTimeRemaining ] = useState({
        minutes: 25,
        seconds: 0
    });

    const startTimer = () => {
        // let counterInterval = setInterval(() => {
        //     let { seconds, minutes } = timeRemaining;
        //     setTimeRemaining({
        //         minutes: minutes--
        //     })
        //     console.log(timeRemaining);
        // }, 1000)
        setTimeRemaining({
            minutes: 24,
            seconds: 10
        })

        console.log(timeRemaining);
    };

    return (
        <>
            <span 
                className="countdown" 
                onClick={startTimer}
            >
                {timeRemaining.minutes}:{timeRemaining.seconds}
            </span>
            <section className="countdown-controls">

            </section>
            <style jsx>{`
                display: inline-block;
                width: 100%;
                text-align: center;
                font-size: 6rem;
                font-weight: 500;
                .countdown-controls {
                    height: 50px;
                    width: 100%;
                    border-radius: 100px;
                    background: white;
                }
            `}</style>
        </>
    )
}

export default Countdown;
