import { useEffect, useState } from 'react';

const TaskPanel = props => {
    // console.log(props)
    return (
        <section className="task-panel">
            <h2>What are you working on?</h2>
            {props.userTasks ? 
                props.userTasks.map(task => {
                    return (
                        <span className="task">
                            {task.name}
                        </span>
                    )
                }) : <span>No tasks added yet.</span>}
            <style jsx>{`
                .task-panel {
                    background-color: white;
                    grid-area: tasks;
                    border-radius: 10px;
                    padding: 20px;
                }
                .task-panel .task {
                    display: inline-block;
                    padding: 3px 5px;
                    border-radius: 100px;
                    background: slateblue;
                    font-size: .8rem;

                }
                h2 {
                    color: #2D2D2D;
                    font-size: 1rem;
                    margin-bottom: 10px;
                }
            `}</style>
        </section>
    )
}

export default TaskPanel;
