import { useState } from 'react';

const Countdown = props => {

const [ timeRemaining, setTimeRemaining ] = useState({
    minutes: 25,
    seconds: 0
})
    const startTimer = (minutes, seconds) => {
        let counterInterval = setInterval(() => {
            let { seconds, minutes } = timeRemaining;
            console.log('counting')
            if (seconds == 0) {
                setTimeRemaining({
                    seconds: 59
                })
            } else {
                setTimeRemaining({
                    seconds: --seconds
                })
            }
        }, 1000)
    }
    return (
        <>
            <span 
                className="countdown" 
                onClick={startTimer}
            >
                {timeRemaining.minutes}:{timeRemaining.seconds}
            </span>
            <section className="countdown-controls">

            </section>
            <style jsx>{`
                display: inline-block;
                width: 100%;
                text-align: center;
                font-size: 7rem;
                font-weight: 500;
                .countdown-controls {
                    height: 50px;
                    width: 100%;
                    border-radius: 100px;
                    background: white;
                }
            `}</style>
        </>
    )
}

export default Countdown;
